from collections import Counter


class Solution:
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: int
        """
        sc = Counter(s)
        res = 0
        flag = True
        for i, v in sc.items():
            if v % 2 == 0:
                res += v
            else:
                # flag 标记是否出现奇数
                flag = False
                res += v - 1

        return res if flag else res + 1


s = ""

print(Solution().longestPalindrome(s))
